import { Conteiner } from "./styled";
import { useSelector, useDispatch } from "react-redux";
import { ProductsConteiner, DiscriptionConteiner } from "../products/styled";
import { removeProduct } from "../../../store/modules/card/actions";

const Cart = () => {

    const card = useSelector((state) => state.card);

    const dispatch = useDispatch();

    const handleClick = (item) => {
        dispatch(removeProduct(item));
    }

    return <Conteiner>
        {card.map((item, idex) => (
            <ProductsConteiner>
                 <img src={item.image}/>
                <DiscriptionConteiner>
                    <p>{item.name}</p>
                    <p>{item.price}</p>
                    <button onClick = {() => handleClick(item)} >Add Produto</button>
                </DiscriptionConteiner>
            </ProductsConteiner>
        ))}
    </Conteiner>
}

export default Cart
