import { useSelector, useDispatch } from "react-redux";
import { ProductsConteiner, DiscriptionConteiner } from "../products/styled";
import { removeProduct } from "../../../store/modules/card/actions";

const Cart = () => {

    const card = useSelector((state) => state.card);

    const dispatch = useDispatch();

    const handleClick = (item) => {
        dispatch(removeProduct(item));
    }

    return<Diplay>
    <div className = "title">
        <h2>KenzieShop</h2>
         <h3>Produtos:</h3>   
    </div>
        <Conteiner>
    {products.map((item,index) => (
        <ProductsConteiner kay = {index} >
            <img src={item.image}/>
            <DiscriptionConteiner>
                <div>
                    <p>{item.name}</p>
                    <p>{item.price.toFixed(2)}</p>
                </div>
                <button onClick = {() => handleClick(item)} >Add Produto</button>
            </DiscriptionConteiner>
        </ProductsConteiner>
    ))}
</Conteiner>
</Diplay>
}

export default Cart
