import { useState } from "react"
import { Conteiner, ProductsConteiner, DiscriptionConteiner } from "./styled"
import { useDispatch } from "react-redux";
import { addProduct } from "../../../store/modules/card/actions";

const Products = () => {

    const  [products] = useState([
        {name: "Processador AMD Ryzen 7 5700G, 3.8GHz", price: 2139.90, image: "https://images.kabum.com.br/produtos/fotos/181089/processador-amd-ryzen-7-5700g-3-8ghz-4-6ghz-max-turbo-am4-video-integrado-8-nucleos-100-100000263box_1627588652_g.jpg"},
        {name: "Smart TV LG 55 4K UHD", price: 2699.00, image: "https://images.kabum.com.br/produtos/fotos/162562/smart-tv-lg-55-4k-uhd-wifi-bluetooth-hdr-inteligencia-artificial-thinq-smart-magic-google-alexa-55up7750psb_1626120430_g.jpg"},
        {name: "Notebook Gamer Acer Nitro 5 AMD Ryzen 5 4600H", price: 5419.90, image: "https://images.kabum.com.br/produtos/fotos/184889/notebook-gamer-ace-nitro-5-amd-ryzen-r5-4600h-six-core-nvidia-geforce-gtx-1650-8gb-ram-ssd-512gb-15-6-win10-preto-an515-44-r54q_1628006097_g.jpg"},
        {name: "Cadeira Gamer Husky Gaming Tempest 500", price: 1149.95, image: "lhttps://images.kabum.com.br/produtos/fotos/134180/cadeira-gamer-tempest-husky-gaming-black-500_1619706678_g.jpg"},
        {name: "Kindle Paperwhite", price: 799.00, image: "https://images.kabum.com.br/produtos/fotos/165059/tablet-samsung-galaxy-a7-lite-32gb-wifi-android-11-tela-de-8-7-grafite-sm-t220nzapzto_1628776861_g.jpg"},
        {name: "Kindle Paperwhite", price: 499.00, image: "link-da-imagem"}
       ]);

    const dispatch = useDispatch();  
    const handleClick = (product) =>{
        dispatch(addProduct(product));
    }

    return<Conteiner>
        {products.map((item,index) => (
            <ProductsConteiner kay = {index} >
                <img src={item.image}/>
                <DiscriptionConteiner>
                    <p>{item.name}</p>
                    <p>{item.price}</p>
                    <button onClick = {() => handleClick(item)} >Add Produto</button>
                </DiscriptionConteiner>
            </ProductsConteiner>
        ))}
    </Conteiner>
}


export default Products;
